interface JQuery {
    treegrid: TreegridStatic;
    on(treegridEvent: 'collapse', callback: () => void);
    on(treegridEvent: 'expand', callback: () => void);
    on(treegridEvent: 'change', callback: () => void);
}


interface TreegridStatic {
    (treegridOption: TreegridOption)           : JQuery;
    (treegridAPI   : "getRootNodes")           : JQuery;
    (treegridAPI   : "getNodeId")              : string;
    (treegridAPI   : "getParentNodeId")        : string;
    (treegridAPI   : "getAllNodes")            : JQuery;
    (treegridAPI   : "getParentNode")          : JQuery;
    (treegridAPI   : "getChildNodes")          : JQuery;
    (treegridAPI   : "getDepth")               : number;
    (treegridAPI   : "isNode")                 : boolean;
    (treegridAPI   : "isLeaf")                 : boolean;
    (treegridAPI   : "isLast")                 : boolean;
    (treegridAPI   : "isFirst")                : boolean;
    (treegridAPI   : "isExpanded")             : boolean;
    (treegridAPI   : "isCollapsed")            : boolean;
    (treegridAPI   : "isOneOfParentsCollapsed"): boolean;
    (treegridAPI   : "expand")                 : JQuery;
    (treegridAPI   : "collapse")               : JQuery;
    (treegridAPI   : "expandRecursive")        : JQuery;
    (treegridAPI   : "collapseRecursive")      : JQuery;
    (treegridAPI   : "expandAll")              : JQuery;
    (treegridAPI   : "collapseAll")            : JQuery;
    (treegridAPI   : "toggle")                 : JQuery;
    (treegridAPI   : "render")                 : JQuery;
}

interface TreegridOption {
    /**
     * Number of column using for tree
     * default:0
     */
    treeColumn?: number;

    /**
     * Initial state of tree
     * 'expanded' - all nodes will be expanded
     * 'collapsed' - all nodes will be collapsed
     * default:expanded
     */
    initialState?: string;

    /**
     * If true you can reload page and tree state was saved
     * default:false
     */
    saveState?: boolean;

    /**
     * 'cookie' - save state to cookie
     * 'hash' - save state to hash
     *  default:cookie
     */
    saveStateMethod?: string;


    /**
     * Name of cookie or hash to save state.
     * default:tree-grid-state
     */
    saveStateName?: string;

    /**
     * HTML Element when you click on that will be collapse/expand branches
     * default:<span class="treegrid-expander"></span>
     */
    expanderTemplate?: string;

    /**
     * Class using for expander element when it expanded
     * default:treegrid-expander-expanded
     */
    expanderExpandedClass?: string;

    /**
     * Class using for expander element when it collapsed
     * default:treegrid-expander-collapsed
     */
    expanderCollapsedClass?: string;

    /**
     * HTML Element that will be placed as padding, depending on the depth of nesting node
     * default:<span class="treegrid-indent"></span>
     */
    indentTemplate?: string;

    /**
     * Function, which will be executed when one of node will be collapsed
     */
    onCollapse?: () => void;

    /**
     * Function, which will be executed when one of node will be expanded
     */
    onExpand?: () => void;

    /**
     * Function, which will be executed when one of node will be expanded or collapsed
     */
    onChange?: () => void;
}